// The vault validator requires parameters: oracle_script_hash and basket_factory_script_hash
// This function applies those parameters and returns the applied script
export function getAppliedVaultScript(
  oracleScriptHash: string,
  basketFactoryScriptHash: string
): string {
  // VaultParams = Constr(0, [oracle_script_hash, basket_factory_script_hash])
  const params = new Constr(0, [oracleScriptHash, basketFactoryScriptHash]);
  
  // Raw unapplied vault script from plutus.json
  const rawVaultScript = "";
  
  // Apply parameters to the script
  const appliedScript = applyParamsToScript(rawVaultScript, [params]);
  
  return applyDoubleCborEncoding(appliedScript);
}

// Get the default vault script with the current oracle and basket factory hashes
export function getDefaultVaultScript(): string {
  return getAppliedVaultScript(ScriptHashes.MockOracle, ScriptHashes.BasketFactory);
}

