// =============================================================================
// TOKEN DECIMALS (for display and calculations)
// =============================================================================

// Basket tokens use 6 decimals (like ADA's lovelace)
// 1.0 token (display) = 1,000,000 units (on-chain)
// 0.001 token = 1,000 units
// 0.000001 token = 1 unit (minimum)
export const TOKEN_DECIMALS = 6;
export const TOKEN_PRECISION = 10n ** BigInt(TOKEN_DECIMALS);  // 1,000,000n

// Conversion helpers
export function tokenToUnits(tokens: number): bigint {
  return BigInt(Math.floor(tokens * Number(TOKEN_PRECISION)));
}

export function unitsToTokens(units: bigint): number {
  return Number(units) / Number(TOKEN_PRECISION);
}

